export function drawTask(): (cfg?: ModelConfig | BpmnG6NodeConfig, group?: IGroup) => IShape {
  return (cfg, group): IShape => {
    const color = ShapeUtil.isTask(cfg.type as ShapeBpmnElementKind) ? '#7FFFD4' : ShapeUtil.isCallActivity(cfg.type as ShapeBpmnElementKind) ? '#6A5ACD' : '#DA70D6';
    const r = 5;
    const width = (cfg.size as number[])[0];
    const height = (cfg.size as number[])[1];
    const shape = group.addShape('rect', {
      attrs: {
        x: 0,
        y: 0,
        width,
        height,
        stroke: color,
        strokeWidth: StyleDefault.STROKE_WIDTH_THIN,
        radius: r,
        anchorPoints: cfg.anchorPoints,
      },
      name: 'main-box',
      draggable: true,
    });

    // left icon
    group.addShape('image', {
      attrs: {
        x: 4,
        y: 2,
        height: 16,
        width: 16,
        cursor: 'pointer',
        // eslint-disable-next-line @typescript-eslint/ban-ts-comment
        // @ts-ignore
        img: ICON_MAP[cfg.type],
      },
      name: 'node-icon',
    });

    // label text
    const textAttr: any = {
      lineHeight: height - 20,
      text: cfg.label,
      fontSize: cfg.labelCfg.style.fontSize,
      fontFamily: cfg.fontFamily,
      fontWeight: cfg.fontWeight,
      /*  Not existing in this shape
       textDecoration: cfg.textDecoration,*/
      fontStyle: cfg.labelCfg.style.fontStyle,
      fill: 'pink',
      shadowOffsetX: 2,
      shadowOffsetY: 2,
      shadowColor: 'blue',
    };
    if (cfg.labelCfg.refX) {
      textAttr.x = cfg.labelCfg.refX;
      textAttr.y = cfg.labelCfg.refY;
    } else {
      textAttr.textBaseline = 'middle';
      textAttr.textAlign = 'center';
      textAttr.x = width / 2;
      textAttr.y = height / 2;
      textAttr.width = width - 20;
      textAttr.height = height - 20;
    }
    group.addShape('text', { attrs: textAttr, name: 'label' });

    // The content list
    // eslint-disable-next-line @typescript-eslint/ban-ts-comment
    // @ts-ignore
    cfg.markers?.forEach((item, index) => {
      // name text
      group.addShape('text', {
        attrs: {
          textBaseline: 'top',
          y: 25,
          x: 24 + index * 60,
          lineHeight: 20,
          text: item.title,
          fill: 'rgba(0,0,0, 0.4)',
        },
        name: `index-title-${index}`,
      });

      // value text
      group.addShape('text', {
        attrs: {
          textBaseline: 'top',
          y: 42,
          x: 24 + index * 60,
          lineHeight: 20,
          text: item.value,
          fill: '#595959',
          fontFamily: 'Arial',
        },
        name: `index-title-${index}`,
      });
    });
    return shape;
  };
}

